let inputs=document.querySelectorAll(".form-control");inputs.forEach(n=>{n.addEventListener("change",n=>{let t=n.target.name;if(!document.querySelector(`input[type='hidden'][value='${t}']`)){let i=document.createElement("input");i.type="hidden";i.name="ModifiedProperties";i.value=t;n.target.parentElement.appendChild(i)}})});